$.get('/search/label?max-results=5', function(e) {
  var a = $(e).find('.blog-posts article'),post_item=''
  if (a.length) {
    var b = a.length,
      r = 5,
      post_html = document.querySelector('#recentPosts ul')
    if (a.length < r) r = a.length
    for(var i=0;i<r;i++){
      var href = $(a[i]).find('a').attr('href'),
		  src = $(a[i]).find('img').attr('src'),
		  title = $(a[i]).find('h2').text(),
          str = '<li><div class="post-image"><div class="img-thumbnail"><a href="'+href+'"><img src="'+src+'" alt="'+title+'"></a></div></div><div class="post-info"><a href="'+href+'">'+title+'</a></div></li>'
      post_item += str
      post_html.innerHTML = post_item
    }
  }
})
var labelname = $('#popularPosts ul').attr('data-related')
$.get('/search/label/'+labelname+'?max-results=5', function(e) {
  var a = $(e).find('.blog-posts article'),related_item=''
  if (a.length) {
    var b = a.length,
      r = 5,
      related_html = document.querySelector('#popularPosts ul')
    if (a.length < r) r = a.length
    let arr = []
    do {
      let num = Math.floor(Math.random() * b)
      arr.push(num)
      arr = arr.filter((item, index) => {
        return arr.indexOf(item) === index
      })
    } while (arr.length < r)
    arr.forEach(function(i) {
      var href = $(a[i]).find('a').attr('href'),
		  src = $(a[i]).find('img').attr('src'),
		  title = $(a[i]).find('h2').text(),
          str = '<li><div class="post-image"><div class="img-thumbnail"><a href="'+href+'"><img src="'+src+'" alt="'+title+'"></a></div></div><div class="post-info"><a href="'+href+'">'+title+'</a></div></li>'
      related_item += str
      related_html.innerHTML = related_item
    })
  }
})
